mapper.update:
update share_bill
set cur_people = #{curPeople}
where bill_id = #{billId}

mapper.select:
select user_id
from share_bill
where bill_id = #{billId}

select bill_id, user_id, cur_people, max_people
from share_bill
where bill_id = #{billId}

select title
from share_bill
where bill_id = #{billId}
